import { type FilterOptions, countries, economicCategories } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuCheckboxItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
  SheetTrigger,
} from "@/components/ui/sheet";
import { Search, ChevronDown, TrendingUp, SlidersHorizontal } from "lucide-react";
import { useState } from "react";
import { useIsMobile } from "@/hooks/use-mobile";

interface FilterControlsProps {
  filters: FilterOptions;
  onFilterChange: (filters: Partial<FilterOptions>) => void;
}

const impactLevels = [
  { value: "high" as const, label: "Alta", color: "bg-impact-high" },
  { value: "medium" as const, label: "Media", color: "bg-impact-medium" },
  { value: "low" as const, label: "Baja", color: "bg-impact-low" },
];

const timePeriods = [
  { value: "today" as const, label: "Hoy" },
  { value: "thisWeek" as const, label: "Esta Semana" },
  { value: "nextWeek" as const, label: "Pr√≥xima Semana" },
  { value: "thisMonth" as const, label: "Este Mes" },
];

export function FilterControls({ filters, onFilterChange }: FilterControlsProps) {
  const isMobile = useIsMobile();
  const [isSheetOpen, setIsSheetOpen] = useState(false);
  const [searchValue, setSearchValue] = useState(filters.search || "");

  const toggleCountry = (countryCode: string) => {
    const current = filters.countries || [];
    const updated = current.includes(countryCode)
      ? current.filter((c) => c !== countryCode)
      : [...current, countryCode];
    onFilterChange({ countries: updated });
  };

  const toggleImpact = (impact: "high" | "medium" | "low") => {
    const current = filters.impacts || [];
    const updated = current.includes(impact)
      ? current.filter((i) => i !== impact)
      : [...current, impact];
    onFilterChange({ impacts: updated });
  };

  const toggleCategory = (category: string) => {
    const current = filters.categories || [];
    const updated = current.includes(category)
      ? current.filter((c) => c !== category)
      : [...current, category];
    onFilterChange({ categories: updated });
  };

  const handleSearchChange = (value: string) => {
    setSearchValue(value);
    onFilterChange({ search: value });
  };

  // Reusable filter content
  const FilterContent = () => (
    <div className="space-y-6">
      <div className="flex flex-col lg:flex-row gap-4">
        {/* Country Filter */}
        <div className="flex-1 min-w-0">
          <label className="block text-xs font-semibold text-foreground uppercase tracking-wider mb-2">
            Pa√≠s / Regi√≥n
          </label>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="outline"
                className="w-full justify-between"
                data-testid="dropdown-country"
              >
                <span className="truncate">
                  {filters.countries && filters.countries.length > 0
                    ? `${filters.countries.length} seleccionados`
                    : "Todos los pa√≠ses"}
                </span>
                <ChevronDown className="ml-2 h-4 w-4 shrink-0" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent className="w-56 z-[200]" align="start">
              <DropdownMenuCheckboxItem
                checked={!filters.countries || filters.countries.length === 0}
                onCheckedChange={() => onFilterChange({ countries: [] })}
                data-testid="checkbox-country-all"
              >
                <span className="font-semibold">Todos los pa√≠ses</span>
              </DropdownMenuCheckboxItem>
              <div className="my-1 h-px bg-border" />
              {countries.map((country) => (
                <DropdownMenuCheckboxItem
                  key={country.code}
                  checked={filters.countries?.includes(country.code)}
                  onCheckedChange={() => toggleCountry(country.code)}
                  data-testid={`checkbox-country-${country.code}`}
                >
                  <div className="flex items-center gap-2">
                    <span className="text-lg">{country.flag}</span>
                    <span className="font-mono text-xs text-muted-foreground">{country.code}</span>
                    <span>{country.name}</span>
                  </div>
                </DropdownMenuCheckboxItem>
              ))}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        {/* Category Filter */}
        <div className="flex-1 min-w-0">
          <label className="block text-xs font-semibold text-foreground uppercase tracking-wider mb-2">
            Categor√≠a Econ√≥mica
          </label>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="outline"
                className="w-full justify-between"
                data-testid="dropdown-category"
              >
                <div className="flex items-center gap-2 truncate">
                  <TrendingUp className="h-4 w-4 shrink-0" />
                  <span className="truncate">
                    {filters.categories && filters.categories.length > 0
                      ? `${filters.categories.length} seleccionadas`
                      : "Todas las categor√≠as"}
                  </span>
                </div>
                <ChevronDown className="ml-2 h-4 w-4 shrink-0" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent className="w-56 z-[200]" align="start">
              <DropdownMenuCheckboxItem
                checked={!filters.categories || filters.categories.length === 0}
                onCheckedChange={() => onFilterChange({ categories: [] })}
                data-testid="checkbox-category-all"
              >
                <div className="flex items-center gap-2">
                  <span className="text-base">üìä</span>
                  <span className="font-semibold">Todas las categor√≠as</span>
                </div>
              </DropdownMenuCheckboxItem>
              <div className="my-1 h-px bg-border" />
              {economicCategories.map((category) => (
                <DropdownMenuCheckboxItem
                  key={category.value}
                  checked={filters.categories?.includes(category.value)}
                  onCheckedChange={() => toggleCategory(category.value)}
                  data-testid={`checkbox-category-${category.value}`}
                >
                  <div className="flex items-center gap-2">
                    <span className="text-base">{category.icon}</span>
                    <span>{category.label}</span>
                  </div>
                </DropdownMenuCheckboxItem>
              ))}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        {/* Search */}
        <div className="flex-1 min-w-0">
          <label className="block text-xs font-semibold text-foreground uppercase tracking-wider mb-2">
            Buscar Eventos
          </label>
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              type="search"
              placeholder="Buscar por evento o pa√≠s..."
              value={searchValue}
              onChange={(e) => handleSearchChange(e.target.value)}
              className="pl-9"
              data-testid="input-search"
            />
          </div>
        </div>
      </div>

      {/* Impact Level and Time Period Filters */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Impact Level Filter */}
        <div>
          <label className="block text-xs font-semibold text-foreground uppercase tracking-wider mb-2">
            Nivel de Impacto
          </label>
          <div className="flex flex-wrap gap-2">
            {impactLevels.map((level) => (
              <Button
                key={level.value}
                variant={filters.impacts?.includes(level.value) ? "default" : "outline"}
                size="sm"
                onClick={() => toggleImpact(level.value)}
                className="gap-2"
                data-testid={`button-impact-${level.value}`}
              >
                <span className={`w-3 h-3 rounded-full ${level.color}`} />
                {level.label}
              </Button>
            ))}
          </div>
        </div>

        {/* Time Period Filter */}
        <div>
          <label className="block text-xs font-semibold text-foreground uppercase tracking-wider mb-2">
            Per√≠odo de Tiempo
          </label>
          <div className="flex flex-wrap gap-2">
            {timePeriods.map((period) => (
              <Button
                key={period.value}
                variant={filters.dateRange === period.value ? "default" : "outline"}
                size="sm"
                onClick={() => onFilterChange({ dateRange: period.value })}
                data-testid={`button-period-${period.value}`}
              >
                {period.label}
              </Button>
            ))}
          </div>
        </div>
      </div>

      {/* Active Filters Summary */}
      {((filters.countries && filters.countries.length > 0) ||
        (filters.impacts && filters.impacts.length > 0) ||
        (filters.categories && filters.categories.length > 0) ||
        filters.search) && (
        <div className="flex items-center gap-2 flex-wrap">
          <span className="text-xs text-muted-foreground">Filtros activos:</span>
          {filters.countries?.map((code) => {
            const country = countries.find((c) => c.code === code);
            return country ? (
              <Badge key={code} variant="secondary" className="gap-1">
                <span className="text-base">{country.flag}</span>
                {country.code}
              </Badge>
            ) : null;
          })}
          {filters.categories?.map((categoryValue) => {
            const category = economicCategories.find((c) => c.value === categoryValue);
            return category ? (
              <Badge key={categoryValue} variant="secondary" className="gap-1">
                <span>{category.icon}</span>
                {category.label}
              </Badge>
            ) : null;
          })}
          {filters.impacts?.map((impact) => {
            const level = impactLevels.find((l) => l.value === impact);
            return level ? (
              <Badge key={impact} variant="secondary" className="gap-1">
                <span className={`w-2 h-2 rounded-full ${level.color}`} />
                {level.label}
              </Badge>
            ) : null;
          })}
          {filters.search && (
            <Badge variant="secondary">
              B√∫squeda: {filters.search}
            </Badge>
          )}
        </div>
      )}
    </div>
  );

  // Mobile: Sheet with trigger button
  if (isMobile) {
    return (
      <div className="space-y-3">
        {/* Time Period Quick Filters - Always visible on mobile */}
        <div className="flex gap-2 overflow-x-auto pb-2 -mx-4 px-4">
          {timePeriods.map((period) => (
            <Button
              key={period.value}
              variant={filters.dateRange === period.value ? "default" : "outline"}
              size="sm"
              onClick={() => onFilterChange({ dateRange: period.value })}
              data-testid={`button-period-${period.value}`}
              className="shrink-0"
            >
              {period.label}
            </Button>
          ))}
        </div>

        {/* Advanced Filters Sheet Trigger */}
        <Sheet open={isSheetOpen} onOpenChange={setIsSheetOpen}>
          <SheetTrigger asChild>
            <Button variant="outline" className="w-full justify-between" data-testid="button-open-filters">
              <div className="flex items-center gap-2">
                <SlidersHorizontal className="w-4 h-4" />
                <span>Filtros Avanzados</span>
              </div>
              {((filters.countries && filters.countries.length > 0) ||
                (filters.impacts && filters.impacts.length > 0) ||
                (filters.categories && filters.categories.length > 0) ||
                filters.search) && (
                <Badge variant="secondary" className="ml-2">
                  {(filters.countries?.length || 0) +
                    (filters.impacts?.length || 0) +
                    (filters.categories?.length || 0) +
                    (filters.search ? 1 : 0)}
                </Badge>
              )}
            </Button>
          </SheetTrigger>
          <SheetContent side="bottom" className="h-[85vh] overflow-y-auto">
            <SheetHeader className="mb-4">
              <SheetTitle>Filtros Avanzados</SheetTitle>
              <SheetDescription>
                Personaliza la visualizaci√≥n de eventos econ√≥micos
              </SheetDescription>
            </SheetHeader>
            <FilterContent />
            <div className="mt-6 pt-4 border-t">
              <Button
                onClick={() => setIsSheetOpen(false)}
                className="w-full"
                data-testid="button-close-filters"
              >
                Aplicar Filtros
              </Button>
            </div>
          </SheetContent>
        </Sheet>
      </div>
    );
  }

  // Desktop: Full filters always visible
  return <FilterContent />;
}

client/src/components/ui/chart.tsx.content:
"use client"

import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "@/lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            const key = `${nameKey || item.name || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)
            const indicatorColor = color || item.payload.fill || item.color

            return (
              <div
                key={item.dataKey}
                className={cn(
                  "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                  indicator === "dot" && "items-center"
                )}
              >
                {formatter && item?.value !== undefined && item.name ? (
                  formatter(item.value, item.name, item, index, item.payload)
                ) : (
                  <>
                    {itemConfig?.icon ? (
                      <itemConfig.icon />
                    ) : (
                      !hideIndicator && (
                        <div
                          className={cn(
                            "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                            {
                              "h-2.5 w-2.5": indicator === "dot",
                              "w-1": indicator === "line",
                              "w-0 border-[1.5px] border-dashed bg-transparent":
                                indicator === "dashed",
                              "my-0.5": nestLabel && indicator === "dashed",
                            }
                          )}
                          style={
                            {
                              "--color-bg": indicatorColor,
                              "--color-border": indicatorColor,
                            } as React.CSSProperties
                          }
                        />
                      )
                    )}
                    <div
                      className={cn(
                        "flex flex-1 justify-between leading-none",
                        nestLabel ? "items-end" : "items-center"
                      )}
                    >
                      <div className="grid gap-1.5">
                        {nestLabel ? tooltipLabel : null}
                        <span className="text-muted-foreground">
                          {itemConfig?.label || item.name}
                        </span>
                      </div>
                      {item.value && (
                        <span className="font-mono font-medium tabular-nums text-foreground">
                          {item.value.toLocaleString()}
                        </span>
                      )}
                    </div>
                  </>
                )}
              </div>
            )
          })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload.map((item) => {
          const key = `${nameKey || item.dataKey || "value"}`
          const itemConfig = getPayloadConfigFromPayload(config, item, key)

          return (
            <div
              key={item.value}
              className={cn(
                "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
              )}
            >
              {itemConfig?.icon && !hideIcon ? (
                <itemConfig.icon />
              ) : (
                <div
                  className="h-2 w-2 shrink-0 rounded-[2px]"
                  style={{
                    backgroundColor: item.color,
                  }}
                />
              )}
              {itemConfig?.label}
            </div>
          )
        })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

