// Simple session ID management using localStorage
export function getSessionId(): string {
  const KEY = 'ec-session-id';
  let sessionId = localStorage.getItem(KEY);

  if (!sessionId) {
    sessionId = `session-${Date.now()}-${Math.random().toString(36).substring(2, 15)}`;
    localStorage.setItem(KEY, sessionId);
  }

  return sessionId;
}

server/utils/date-range.ts.content:
import { format, startOfDay, endOfDay, startOfWeek, endOfWeek, startOfMonth, endOfMonth, addWeeks } from "date-fns";
import { toZonedTime, fromZonedTime } from "date-fns-tz";

export interface DateRange {
  startUtc: Date;
  endUtc: Date;
  startDate: string; // YYYY-MM-DD format
  endDate: string; // YYYY-MM-DD format
  localLabel: string;
}

/**
 * Calculate date range based on period and timezone
 * @param period - "today" | "thisWeek" | "nextWeek" | "thisMonth"
 * @param timezone - IANA timezone string (default: "UTC")
 * @returns DateRange with UTC timestamps and date strings
 */
export function calculateDateRange(
  period: "today" | "thisWeek" | "nextWeek" | "thisMonth" = "today",
  timezone: string = "UTC"
): DateRange {
  // Get current time in the specified timezone
  const nowUtc = new Date();
  const nowInZone = toZonedTime(nowUtc, timezone);

  let startLocal: Date;
  let endLocal: Date;
  let localLabel: string;

  switch (period) {
    case "today":
      startLocal = startOfDay(nowInZone);
      endLocal = endOfDay(nowInZone);
      localLabel = `Hoy (${format(nowInZone, "dd/MM/yyyy")})`;
      break;

    case "thisWeek":
      // Monday to Sunday
      startLocal = startOfWeek(nowInZone, { weekStartsOn: 1 });
      endLocal = endOfWeek(nowInZone, { weekStartsOn: 1 });
      localLabel = `Esta Semana (${format(startLocal, "dd/MM")} - ${format(endLocal, "dd/MM")})`;
      break;

    case "nextWeek":
      const nextWeekStart = addWeeks(startOfWeek(nowInZone, { weekStartsOn: 1 }), 1);
      startLocal = nextWeekStart;
      endLocal = endOfWeek(nextWeekStart, { weekStartsOn: 1 });
      localLabel = `Pr√≥xima Semana (${format(startLocal, "dd/MM")} - ${format(endLocal, "dd/MM")})`;
      break;

    case "thisMonth":
      startLocal = startOfMonth(nowInZone);
      endLocal = endOfMonth(nowInZone);
      localLabel = `Este Mes (${format(nowInZone, "MMMM yyyy")})`;
      break;

    default:
      startLocal = startOfDay(nowInZone);
      endLocal = endOfDay(nowInZone);
      localLabel = `Hoy`;
  }

  // Convert back to UTC for timestamp filtering
  const startUtc = fromZonedTime(startLocal, timezone);
  const endUtc = fromZonedTime(endLocal, timezone);

  // IMPORTANT: Use date strings from local time to avoid timezone issues
  // This ensures "today" in NY shows only events with date="2025-11-11",
  // not events from "2025-11-12" that happen to fall within the UTC range
  const startDate = format(startLocal, "yyyy-MM-dd");
  const endDate = format(endLocal, "yyyy-MM-dd");

  return {
    startUtc,
    endUtc,
    startDate,
    endDate,
    localLabel,
  };
}

/**
 * Get date range for background refresh jobs
 * @param days - Number of days forward from today
 * @returns DateRange for job execution
 */
export function getRefreshRange(days: number): DateRange {
  const now = new Date();
  const start = startOfDay(now);
  const end = new Date(now);
  end.setDate(end.getDate() + days);

  return {
    startUtc: start,
    endUtc: endOfDay(end),
    startDate: format(start, "yyyy-MM-dd"),
    endDate: format(end, "yyyy-MM-dd"),
    localLabel: `${days} days forward`,
  };
}

server/services/cache-coordinator.ts.content:
import { refreshEventsCache } from "./events-cache";
import { storage } from "../storage";
import { getRefreshRange } from "../utils/date-range";

// Lock to prevent concurrent refresh jobs
let isRefreshing = false;
let lastRefreshTime = 0;

const COUNTRIES = ['USA', 'EUR', 'DEU', 'FRA', 'ESP', 'GBR', 'CHN', 'JPN'];

/**
 * Nightly job: Refresh events for current month + next 60 days
 */
export async function refreshMonthlyCache(): Promise<void> {
  if (isRefreshing) {
    console.log("Monthly refresh skipped: another refresh is in progress");
    return;
  }

  try {
    isRefreshing = true;
    console.log("Starting monthly cache refresh...");

    // Start from beginning of current month
    const now = new Date();
    const startOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
    const fromDate = startOfMonth.toISOString().split('T')[0];

    // Refresh for 90 days forward (3 months)
    const range = getRefreshRange(90);
    await refreshEventsCache(fromDate, range.endDate, COUNTRIES);

    // Clean up old events (keep 180 days / 6 months)
    await storage.deleteOldCachedEvents(180);

    lastRefreshTime = Date.now();
    console.log("Monthly cache refresh complete");
  } catch (error) {
    console.error("Monthly refresh failed:", error);
  } finally {
    isRefreshing = false;
  }
}

/**
 * Daily job: Refresh events for today ¬±7 days (14-day window)
 * Runs at 14:00 UTC, 12 hours offset from monthly job at 02:00 UTC
 */
export async function refreshDailyWindow(): Promise<void> {
  if (isRefreshing) {
    console.log("Daily refresh skipped: another refresh is in progress - will retry in 30 minutes");
    // Retry after 30 minutes if skipped due to concurrent refresh
    setTimeout(refreshDailyWindow, 30 * 60 * 1000);
    return;
  }

  try {
    isRefreshing = true;
    console.log("Starting daily window refresh (14-day range)...");

    // Today ¬±7 days (14 days total)
    const range = getRefreshRange(14);
    const sevenDaysAgo = new Date();
    sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

    const from = sevenDaysAgo.toISOString().split('T')[0];
    const to = range.endDate;

    await refreshEventsCache(from, to, COUNTRIES);

    lastRefreshTime = Date.now();
    console.log("Daily window refresh complete");
  } catch (error) {
    console.error("Daily refresh failed:", error);
  } finally {
    isRefreshing = false;
  }
}

/**
 * Initialize background refresh jobs
 */
export function initializeRefreshJobs(): void {
  console.log("Initializing cache refresh jobs...");

  // Run monthly refresh daily at 2 AM
  const runNightly = () => {
    const now = new Date();
    const nextRun = new Date();
    nextRun.setHours(2, 0, 0, 0);

    if (nextRun <= now) {
      nextRun.setDate(nextRun.getDate() + 1);
    }

    const delay = nextRun.getTime() - now.getTime();

    setTimeout(() => {
      refreshMonthlyCache();
      // Schedule next run
      setInterval(refreshMonthlyCache, 24 * 60 * 60 * 1000); // Every 24 hours
    }, delay);

    console.log(`Monthly refresh scheduled for ${nextRun.toISOString()}`);
  };

  // Run daily window refresh at 14:00 UTC (12 hours after monthly job)
  const runDaily = async () => {
    // Check if we should run immediately on startup
    const latestDate = await storage.getLatestCachedDate();

    if (latestDate) {
      // Cache exists, run immediate refresh on startup
      console.log("Running immediate daily window refresh on startup...");
      refreshDailyWindow();
    } else {
      console.log("Skipping initial daily refresh - waiting for bootstrap");
    }

    // Schedule next run at 14:00 UTC
    const now = new Date();
    const nextRun = new Date();
    nextRun.setUTCHours(14, 0, 0, 0);

    if (nextRun <= now) {
      nextRun.setDate(nextRun.getDate() + 1);
    }

    const delay = nextRun.getTime() - now.getTime();

    setTimeout(() => {
      refreshDailyWindow();
      // Schedule next run every 24 hours
      setInterval(refreshDailyWindow, 24 * 60 * 60 * 1000);
    }, delay);

    console.log(`Daily window refresh scheduled for ${nextRun.toISOString()}`);
  };

  // Start jobs
  runNightly();
  runDaily();

  // Run initial bootstrap if no data exists
  checkInitialData();
}

/**
 * Check if initial data exists, if not, trigger full month refresh
 */
async function checkInitialData(): Promise<void> {
  try {
    const latestDate = await storage.getLatestCachedDate();

    if (!latestDate) {
      if (isRefreshing) {
        console.log("Initial data check skipped: another refresh is in progress");
        return;
      }

      isRefreshing = true;
      console.log("No cached data found, running initial month + next week refresh...");

      try {
        // Load current month + next 67 days (~2 months to ensure full coverage)
        const now = new Date();
        const startOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
        const fromDate = startOfMonth.toISOString().split('T')[0];
        const range = getRefreshRange(67);

        await refreshEventsCache(fromDate, range.endDate, COUNTRIES);

        lastRefreshTime = Date.now();
        console.log("Initial data refresh complete");
      } finally {
        isRefreshing = false;
      }
    } else {
      console.log(`Latest cached date: ${latestDate}`);
    }
  } catch (error) {
    console.error("Error checking initial data:", error);
    isRefreshing = false;
  }
}

/**
 * Get cache status
 */
export function getCacheStatus(): {
  isRefreshing: boolean;
  lastRefreshTime: number;
  lastRefreshAge: string;
} {
  const age = lastRefreshTime ? Date.now() - lastRefreshTime : 0;
  const ageMinutes = Math.round(age / 1000 / 60);

  return {
    isRefreshing,
    lastRefreshTime,
    lastRefreshAge: ageMinutes > 0 ? `${ageMinutes} minutes ago` : "Never",
  };
}

